def calculate(puzzle)
  newarray = puzzle.split(//) #creates an array
  total = 0
  position = 0

  for i in newarray #iterates over each item in the array?
    if i == "("
      total = total + 1
      position = position + 1
      puts "this is #{total}"
      puts position
    else
      total = total - 1
      position = position + 1
      puts "total is #{total}"
      puts position
      end
   end
end



