def calculate(puzzle)
  newarray = puzzle.split(//) #creates an array
  total = 0
  position = Array.new
  
  for i in newarray #iterates over each item in the array?
    if i == "("
      total = total + 1
      position.push(total)
    else
      total = total - 1
      position.push(total)
      end
   end
   puts total
end

